The example below shows a sample collection of channels that would be stopped from another thread.

  public void StopDialOnAllChannels()
  {
  // This code is probably in a monitor or master thread, typically you would have a chhanel resources in a collection,
  // determine that a dial needs to stop on a channel or group of channels.  Here we have a list of channels for example.
  List<ChannelResource>
    ChannelList = new List<ChannelResource>();

      // Loop through the whole list
      foreach (ChannelResource cr in ChannelList)
      {
          // Stop dial on every channel one at a time.
          cr.StopDial();
      }
  }


  Public Sub StopDialOnAllChannels()
    ' This code is probably in a monitor or master thread, typically you would have a chhanel resources in a collection,
    ' determine that a dial needs to stop on a channel or group of channels.  Here we have a list of channels for example.
    Dim ChannelList As New List(Of ChannelResource)()

    ' Loop through the whole list
    For Each cr As ChannelResource In ChannelList
      ' Stop dial on every channel one at a time.
      cr.StopDial()
    Next
  End Sub

The example below shows a setting the status and data elements for the channel for the dashboard.

  public void SetupChannelForDashBoard()
  {
    // To get a channel, you must first get a link to a Telephony Server.  You may need to pass a username password here.
    TelephonyServer m_TelephonyServer = new TelephonyServer();

    // Get your channel resource from the server
    ChannelResource m_ChannelResource = m_TelephonyServer.GetChannel();

    // Set the status to "available" for dashboard Users
    m_ChannelResource.SetMonitorStatus("Available");

    // Set a data element of AgentID to 1 and set flag to clear exiting key if exists
    m_ChannelResource.SetMonitorData("AgentID", "1", true);

    // Set a data element of AgentName to "Bugs Bunny" and set flag to clear exiting key if exists
    m_ChannelResource.SetMonitorData("AgentName", "Bugs Bunny", true);
  }


  Public Sub SetupChannelForDashBoard()
    ' To get a channel, you must first get a link to a Telephony Server.  You may need to pass a username password here.
    Dim m_TelephonyServer As New TelephonyServer()

    ' Get your channel resource from the server
    Dim m_ChannelResource As ChannelResource = m_TelephonyServer.GetChannel()

    ' Set the status to "available" for dashboard Users
    m_ChannelResource.SetMonitorStatus("Available")

    ' Set a data element of AgentID to 1 and set flag to clear exiting key if exists
    m_ChannelResource.SetMonitorData("AgentID", "1", True)

    ' Set a data element of AgentName to "Bugs Bunny" and set flag to clear exiting key if exists
    m_ChannelResource.SetMonitorData("AgentName", "Bugs Bunny", True)
  End Sub

